import React, { useReducer } from "react";
import Infoinput from "./components/Infoinput";
import SelectInput from "./components/SelectInput";
import Speech from "./components/Speech";
import "./css/index.css";
function reducer(state, action) {
  switch (action.type) {
    case "company":
      return { ...state, title: action.company };
    case "number":
      return { ...state, title: action.number };
    case "history":
      return { ...state, title: action.history };
    case "sector":
      return { ...state, title: action.sector };
    case "region":
      return { ...state, title: action.region };
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}
function Request() {
  const [info, dispatch] = useReducer(reducer, {
    company: "",
    number: "",
    history: "",
    sector: "",
    region: "",
  });
  return (
    <div className="request">
      <div className="top">
        <div className="wrapper">
          <div className="left">
            <div className="category">상담신청</div>
            <div className="title">
              정부지원금으로 스마트하게 <br /> 사업 운영하는 노하우!
            </div>
            <div className="sub">
              김과장컨설팅은 고객님의 사업장에 최적화된 <br /> 경영지원 서비스를
              제공합니다.
            </div>
          </div>
          <div className="right">
            <img src="/assets/request/document.svg" alt="" />
          </div>
        </div>
      </div>
      <div className="content">
        <div className="wrapper">
          <div className="notice">
            <div className="circle" />
            <div className="text">
              필수입력 항목입니다. 입력항목에 따라, 사업안내가 제한적일 수
              있습니다 .
            </div>
          </div>
          <div className="section1">
            <div className="content-head">
              <img src="/assets/request/company.svg" alt="" />
              <div className="text">기업정보</div>
            </div>
            <div className="company-wrapper">
              <div className="left">
                {section1Input.map(
                  ({ title, type, content, placeholder }, idx) => {
                    return content !== "select" ? (
                      <Infoinput
                        key={idx}
                        title={title}
                        type={type}
                        content={content}
                        placeholder={placeholder}
                      />
                    ) : (
                      <SelectInput key={idx} type={type} title={title} />
                    );
                  }
                )}
              </div>
              <div className="right">
                {section1Speech.map((item, idx) => {
                  return <Speech key={idx} content={item} />;
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Request;

const section1Input = [
  {
    title: "회사명",
    type: "company",
    content: "text",
    placeholder: "사업자등록증에 표기된 회사명  입력",
  },
  {
    title: "사업자등록번호",
    type: "number",
    content: "number",
    placeholder: "- 없이 숫자만 입력해주세요",
  },
  {
    title: "업력",
    type: "history",
    content: "select",
    placeholder: "업력 선택",
  },
  {
    title: "업종",
    type: "sector",
    content: "select",
    placeholder: "업종 선택",
  },
  {
    title: "지역",
    type: "region",
    content: "select",
    placeholder: "지역 선택",
  },
];
const section1Speech = [
  <div className="text">
    업력에 따라 수혜 가능한 지원사업이 달라집니다. <br /> 정확한 업력을
    입력해주세요.
  </div>,
  <div className="text">
    업종에 따라 수혜 가능한 지원사업이 달라집니다. <br /> 정확한 업종을
    입력해주세요.
  </div>,
  <div className="text long">
    사업장 소재지에 따라 수혜 가능한 지원사업이 달라집니다. <br /> 정확한 지역을
    입력해주세요.
  </div>,
];
