import React, { useState } from "react";
import styled, { css } from "styled-components";
import Header from "../Header/Header";
const arr = [
  "기업지원금 컨설팅",
  "벤처 인증",
  "바우처사업",
  "ISO / HACCP 인증",
  "연구개발전담부서",
];
const Wrapper = styled.div`
  padding-top: 114px;
  & > nav {
    width: 100%;
    border-bottom: solid 1px #dbdbdb;
    padding-bottom: 13.5px;
    & > div {
      width: 993px;
      margin: 0 auto;
      display: grid;
      grid-template-columns: repeat(5, 169px);
      column-gap: 30px;
    }
  }
`;
const Navbox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 17px;
  font-weight: 500;
  color: #434343;
  cursor: pointer;
  ${(props) => {
    return css`
      color: ${props.now};
    `;
  }}
`;
function Service() {
  const [now, setNow] = useState("기업지원금 컨설팅");
  return (
    <Wrapper>
      <header>
        <Header />
      </header>
      <nav>
        <div>
          {arr.map((item, idx) => {
            return (
              <Navbox key={idx} now={now} item={item}>
                {item}
              </Navbox>
            );
          })}
        </div>
      </nav>
    </Wrapper>
  );
}

export default Service;
