import React from "react";
import styled from "styled-components";
const Wrapper = styled.section`
  & > .top {
    position: relative;
    padding-top: 163px;
    padding-left: 187px;
    box-sizing: border-box;
    display: flex;
    & > .back {
      width: 100%;
      height: 621px;
      background-color: #47d99b;
      border-bottom-left-radius: 200px;
      position: absolute;
      top: 0;
      left: 0;
      z-index: -1;
    }
    & > .left {
      display: flex;
      flex-direction: column;
      & > .sub-title {
        font-size: 30px;
        font-weight: bold;
        line-height: 1.43;
      }
      & > .title {
        margin-top: 11px;
        margin-bottom: 21px;
        font-size: 55px;
        font-family: "ygo";
      }
      & > .sub {
        font-size: 17px;
        font-weight: 500;
        line-height: 1.47;
      }
      & > button {
        width: 249px;
        height: 54px;
        background-color: white;
        border-radius: 57px;
        font-size: 18px;
        font-weight: bold;
        margin-top: 30px;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        & > img {
          width: 36.3px;
          height: 21.7px;
          margin-left: 17px;
        }
      }
    }
    & > .right {
      width: 473.4px;
      height: 512px;
    }
  }
`;

function Section1() {
  return (
    <Wrapper>
      <article className="top">
        <div className="back" />
        <div className="left">
          <span className="sub-title">
            기업과 창업자들을 향한 <br /> 강력하고 알찬 기획의 시작
          </span>
          <span className="title">김과장 컨설팅</span>
          <span className="sub">
            스타트업의 생존과 사업화 성공을 위해, <br /> 기업 컨설팅 전문가
            김과장이 달려갑니다.
          </span>
          <button>
            <span> 김과장에게 문의하기</span>
            <img src="/assets/right-arrow.svg" alt="이동" />
          </button>
        </div>
        <figure className="right">
          <img
            src="/assets/kimmanager.webp"
            srcSet="/assets/kimmanager@2x.webp 2x , /assets/kimmanager@3x.webp 3x"
            alt=""
          />
        </figure>
      </article>
    </Wrapper>
  );
}

export default Section1;
