import React, { useCallback, useReducer } from "react";
import Infoinput from "./components/Infoinput";
import SelectInput from "./components/SelectInput";
import Speech from "./components/Speech";
import "./css/index.css";
function reducer(state, action) {
  switch (action.type) {
    case "company":
      return { ...state, company: action.company };
    case "number":
      return { ...state, number: action.number };
    case "history":
      return { ...state, history: action.history };
    case "sector":
      return { ...state, sector: action.sector };
    case "region":
      return { ...state, region: action.region };
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}
function Request() {
  const [info, dispatch] = useReducer(reducer, {
    company: "",
    number: "",
    history: "",
    sector: "",
    region: "",
  });
  const __company = useCallback(
    (e) => {
      dispatch({
        type: "company",
        company: e,
      });
    },
    [dispatch]
  );
  const __number = useCallback(
    (e) => {
      dispatch({
        type: "number",
        number: e,
      });
    },
    [dispatch]
  );

  const __history = useCallback(
    (e) => {
      dispatch({
        type: "history",
        history: e,
      });
    },
    [dispatch]
  );
  const __sector = useCallback((e) => {
    dispatch({
      type: "sector",
      sector: e,
    });
  }, []);

  const __region = useCallback((e) => {
    dispatch({
      type: "region",
      region: e,
    });
  }, []);

  return (
    <div className="request">
      <div className="top">
        <div className="wrapper">
          <div className="left">
            <div className="category">상담신청</div>
            <div className="title">
              정부지원금으로 스마트하게 <br /> 사업 운영하는 노하우!
            </div>
            <div className="sub">
              김과장컨설팅은 고객님의 사업장에 최적화된 <br /> 경영지원 서비스를
              제공합니다.
            </div>
          </div>
          <div className="right">
            <img src="/assets/request/document.svg" alt="" />
          </div>
        </div>
      </div>
      <div className="content">
        <div className="wrapper">
          <div className="notice">
            <div className="circle" />
            <div className="text">
              필수입력 항목입니다. 입력항목에 따라, 사업안내가 제한적일 수
              있습니다 .
            </div>
          </div>
          <div className="section1">
            <div className="content-head">
              <img src="/assets/request/company.svg" alt="" />
              <div className="text">기업정보</div>
            </div>
            <div className="company-wrapper">
              <div className="left">
                {section1Input.map(
                  ({ title, type, content, placeholder, arr }, idx) => {
                    return content !== "select" ? (
                      <Infoinput
                        key={idx}
                        title={title}
                        type={type}
                        content={content}
                        placeholder={placeholder}
                      />
                    ) : (
                      <SelectInput
                        key={idx}
                        title={title}
                        type={type}
                        content={content}
                        placeholder={placeholder}
                        arr={arr}
                        info={
                          type === "history"
                            ? info.history
                            : type === "sector"
                            ? info.sector
                            : info.region
                        }
                        dispatch={
                          type === "history"
                            ? __history
                            : type === "sector"
                            ? __sector
                            : __region
                        }
                      />
                    );
                  }
                )}
              </div>
              <div className="right">
                {section1Speech.map((item, idx) => {
                  return <Speech key={idx} content={item} />;
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Request;

const section1Input = [
  {
    title: "회사명",
    type: "company",
    content: "text",
    placeholder: "사업자등록증에 표기된 회사명  입력",
  },
  {
    title: "사업자등록번호",
    type: "number",
    content: "number",
    placeholder: "- 없이 숫자만 입력해주세요",
  },
  {
    title: "업력",
    type: "history",
    content: "select",
    placeholder: "업력 선택",
    arr: ["예비창업자", "1년 미만", "3년 미만", "7년 미만", "7년 이상"],
  },
  {
    title: "업종",
    type: "sector",
    content: "select",
    placeholder: "업종 선택",
    arr: [
      {
        title: "제조업",
        img: <img src="/assets/request/sector1.svg" alt="" />,
      },
      {
        title: "정보통신업 (IT)",
        img: <img src="/assets/request/sector2.svg" alt="" />,
      },
      {
        title: "F&B, 프랜차이즈",
        img: <img src="/assets/request/sector3.svg" alt="" />,
      },
      {
        title: "도소매 유통 판매",
        img: <img src="/assets/request/sector4.svg" alt="" />,
      },
      {
        title: "엔터테인먼트, 여행, 예술",
        img: <img src="/assets/request/sector5.svg" alt="" />,
      },
      {
        title: "공공 행정",
        img: <img src="/assets/request/sector6.svg" alt="" />,
      },
      {
        title: "건설 및 기간 산업",
        img: <img src="/assets/request/sector7.svg" alt="" />,
      },
      {
        title: "세무, 노무, 법무, 행정",
        img: <img src="/assets/request/sector8.svg" alt="" />,
