import React from "react";
import styled from "styled-components";
const Wrapper = styled.section`
  & > .top {
    position: relative;
    padding-top: 163px;
    padding-left: 187px;
    box-sizing: border-box;
    & > .back {
      width: 100%;
      height: 621px;
      background-color: #47d99b;
      border-bottom-left-radius: 200px;
      position: absolute;
      top: 0;
      left: 0;
      z-index: -1;
    }
    & > .left {
      display: flex;
      flex-direction: column;
      & > .sub-title {
        font-size: 30px;
        font-weight: bold;
        line-height: 1.43;
      }
      & > .title {
        margin-top: 11px;
        margin-bottom: 21px;
        font-size: 55px;
        font-family: "ygo";
      }
      & > .sub {
        font-size: 17px;
        font-weight: 500;
      }
    }
  }
`;

function Section1() {
  return (
    <Wrapper>
      <article className="top">
        <div className="back" />
        <div className="left">
          <span className="sub-title">
            기업과 창업자들을 향한 <br /> 강력하고 알찬 기획의 시작
          </span>
          <span className="title">김과장 컨설팅</span>
          <span className="sub">
            스타트업의 생존과 사업화 성공을 위해, <br /> 기업 컨설팅 전문가
            김과장이 달려갑니다.
          </span>
          <button>김과장에게 문의하기</button>
        </div>
      </article>
    </Wrapper>
  );
}

export default Section1;
