import React, { useCallback } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import MainPopup from "./components/MainPopup";
const Wrapper = styled.section`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10 00;
  & > .back {
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    background-color: black;
    opacity: 0.5;
  }
  & > .box {
    overflow: hidden;
    position: relative;
    z-index: 2000;
    width: 320px;
    height: 454px;
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: white;
    box-sizing: border-box;
    text-align: center;
    & > .cancel {
      position: absolute;
      right: 19px;
      top: 23px;
      width: 24px;
      height: 24px;
      cursor: pointer;
      z-index: 10;
    }
    & > .main-popup {
      padding-top: 56px;
      & > .content {
        width: fit-content;
        display: grid;
        grid-template-columns: repeat(4, 320px);
        position: absolute;
        left: 0;
        & > .card-wrapper {
          display: flex;
          flex-direction: column;
          align-items: center;
          & > .title {
            font-size: 23px;
            font-weight: bold;
            line-height: 1.43;
          }
          & > .sub {
            font-size: 14px;
            line-height: 1.43;
            margin: 6px 0 18px 0;
          }
          & > .img-wrapper {
            width: 128px;
            height: 128px;
            background-color: #f5f6f8;
            display: flex;
            align-items: center;
            box-sizing: border-box;
            border-radius: 128px;
            & > img {
              width: fit-content;
            }
          }
        }
      }
      & > button {
        position: absolute;
        width: 31px;
        top: 236px;
        background-color: unset;
      }
      & > .left {
        left: 17px;
      }
      & > .right {
        right: 17px;
        transform: rotate(180deg);
      }
    }
  }
`;
function Popup({ type }) {
  const dispatch = useDispatch();
  const cancel = useCallback(() => {
    dispatch({
      type: "config/isPopup",
      payload: false,
    });
  }, [dispatch]);
  return (
    <Wrapper>
      <div className="back" />
      <div className="box">
        <figure className="cancel" onClick={cancel}>
          <img src="/assets/cancel.svg" alt="닫기" />
        </figure>
        {type === "main" ? <MainPopup /> : undefined}
      </div>
    </Wrapper>
  );
}

export default Popup;
