import React from "react";
import styled from "styled-components";
const Wrapper = styled.section`
  height: 1631px;
  background-color: #47d99b;
  padding: 152px 189px 172px 189px;
  box-sizing: border-box;
  & > .title {
    text-align: center;
    font-size: 35px;
    font-family: "ygo";
    line-height: 1.43;
    & > .focus-title {
      font-size: 60px;
      width: 369px;
      height: 69px;
      background-color: white;
      margin-top: 19px;
      line-height: unset;
      padding: 5px;
    }
  }
`;
const Bubble = styled.div`
  .bubble {
    position: relative;
    width: 750px;
    height: 230px;
    padding: 0px;
    background: #ffffff;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    border-radius: 10px;
  }

  .bubble:after {
    content: "";
    position: absolute;
    border-style: solid;
    border-width: 18px 27px 18px 0;
    border-color: transparent #ffffff;
    display: block;
    width: 0;
    z-index: 1;
    left: -27px;
    top: 59px;
  }
`;
function Section3() {
  return (
    <Wrapper>
      <div className="title">
        <span>
          어디서부터 시작해야 할지 <br /> 벌써부터 막막한 당신을 위한
          <br />
        </span>
        <span className="focus-title">김과장컨설팅</span>
      </div>
      <article className="bubble-wrapper">
        <Bubble></Bubble>
      </article>
    </Wrapper>
  );
}

export default Section3;
