import React from "react";
import styled, { css } from "styled-components";

const arr = [
  {
    title: `벤처인
회원가입`,
    img: "logo1.png",
    width: "154px",
    height: "56px",
  },
  {
    title: `사업계획서 및
재무제표 입력`,
    img: "logo2.svg",
    width: "88.2px",
    height: "79.3px",
  },
  {
    title: `서류접수 및
방문실사`,
    img: "logo3.svg",
    width: "67.2px",
    height: "81.3px",
  },
  {
    title: `벤처기업
인증서`,
    img: "logo4.svg",
    width: "84.7px",
    height: "88px",
  },
];
const Wrapper = styled.section`
  height: 1264px;
  background-color: #f8f8f8;
  & > div {
    width: 993px;
    height: 100%;
    margin: 0 auto;
    & > .top {
      & > .circle-wrapper {
        display: grid;
        grid-template-columns: repeat(4, 200px);
        column-gap: 60px;
      }
    }
  }
`;
const Box = styled.div`
  position: relative;
  & > .arrow {
    width: 25.6px;
    height: 20.8px;
    position: absolute;
    top: 50%;
    right: -42px;
  }
  & > .img-wrapper {
    width: 200px;
    height: 200px;
    background-color: white;
    border-radius: 200px;
    display: flex;
    justify-content: center;
    align-items: center;
    ${(props) => {
      return css`
        & > img {
          width: ${props.wid};
          height: ${props.hei};
        }
      `;
    }}
  }
`;
function Bottom() {
  return (
    <Wrapper>
      <div>
        <div className="top">
          <div className="title">벤처인증은 어떻게 진행되나요?</div>
          <div className="sub">
            벤처확인 신청 및 결과 조회 등의 절차는 벤처인 웹사이트를 통해
            인터넷으로만 이루어집니다.
          </div>
          <div className="circle-wrapper">
            {arr.map(({ title, img, width, height }, idx) => {
              return (
                <Box key={idx} className="box" wid={width} hei={height}>
                  <figure className="img-wrapper">
                    {idx === 0 ? (
                      <img
                        src="/assets/service/venture/logo1.jpg"
                        srcSet="/assets/service/venture/logo1@2x.jpg 2x , /assets/service/venture/logo1@3x.jpg 3x"
                      />
                    ) : (
                      <img src={`/assets/service/venture/${img}`} />
                    )}
                  </figure>
                  {idx !== 3 ? (
                    <figure className="arrow">
                      <img
                        src="/assets/service/venture/right-arrow.svg"
                        alt=""
                      />
                    </figure>
                  ) : undefined}
                </Box>
              );
            })}
          </div>
        </div>
        <div className="bottom"></div>
      </div>
    </Wrapper>
  );
}

export default Bottom;
