import React from "react";
import styled from "styled-components";
const arr = [
  {
    tag: "# 기업하길 잘했네",
    title: "기업지원금",
    sub: `사업에 필요한 각종지원금
정책자금 , 기업대출 등 
김과장이 꼼꼼하게 따져 제안합니다.`,
  },
  {
    tag: "#스타트업 치트키",
    title: "벤처 인증",
    sub: `무엇부터 해야하나?
복잡한 인증절차도 
김과장과 함께라면 일사천리.`,
  },
  {
    tag: "#당신에게 ‘바우처사업’이란?",
    title: "바우처사업",
    sub: `필요한 곳 요긴하게
바우처를 활용하여
기업경쟁력 높여드릴게요.`,
  },
  {
    tag: "# 인증 업무",
    title: "ISO / HACCP 인증",
    sub: `경영시스템 인증업무도 막힘없이
김과장이 척척`,
  },
  {
    tag: "# 기업부설연구소",
    title: "연구개발전담부서",
    sub: `중소기업의 필수아이템, 
기업이 가진 잠재적가치를 생산할 수
있도록 김과장컨설팅이 날개를 달아드립니다. `,
  },
];
const Wrapper = styled.section`
  width: 100%;
  height: 1614px;
  background-color: #f8f8f8;
  & > div {
    width: 993px;
    height: 100%;
    margin: 0 auto;
    display: flex;
    padding-top: 174px;
    box-sizing: border-box;
    & > .left {
      position: sticky;
      top: 64px;
      height: fit-content;
      & > figure {
        width: 232.6px;
        height: 253.3px;
      }
      & > .title {
        margin-top: 38px;
        color: #434343;
        font-size: 40px;
        line-height: 1.38;
        color: #434343;
        font-family: "ygo";
      }
      & > .sub {
        margin-top: 20px;
        font-size: 17px;
        line-height: 1.47;
      }
    }
    & > .right {
      margin-left: 148px;
      display: grid;
      row-gap: 26px;
      & > .box {
        width: 521px;
        height: 224px;
        background-color: white;
        border-radius: 20px;
        .speech-bubble {
          position: relative;
          background: #f4f4f4;
          border-radius: 0.4em;
          width: fit-content;
          padding: 11px 21px;
        }

        .speech-bubble:after {
          content: "";
          position: absolute;
          top: 0;
          left: 50%;
          width: 0;
          height: 0;
          border: 17px solid transparent;
          border-bottom-color: #f4f4f4;
          border-top: 0;
          border-left: 0;
          margin-left: -8.5px;
          margin-top: -17px;
        }
      }
    }
  }
`;
function Section4() {
  return (
    <Wrapper>
      <div>
        <div className="left">
          <figure>
            <img src="/assets/about-kim.svg" alt="" />
          </figure>
          <div className="title">
            김과장을 <br /> 만나면 생기는 일
          </div>
          <div className="sub">
            김과장은 늘 준비되어 있습니다. <br /> 복잡하고 번거로운 기업 업무!{" "}
            <br /> 김과장이 ‘쉽고. 빠르고, 정확하게’ 지원합니다.
          </div>
        </div>
        <div className="right">
          {arr.map(({ tag, title, sub }, idx) => {
            return (
              <div key={idx} className="box">
                <div className="speech-bubble">{tag}</div>
              </div>
            );
          })}
        </div>
      </div>
    </Wrapper>
  );
}

export default Section4;
