import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import styled from "styled-components";

const menuarr = [
  { title: "김과장컨설팅", link: "/about" },
  { title: "서비스소개", link: "/service" },
  { title: "고객지원", link: "/support" },
];
const Wrapper = styled.div`
  display: flex;
  z-index: 1000;
  box-sizing: border-box;
  align-items: center;
  padding: 0 74px;
  width: 100%;
  height: 64px;
  justify-content: space-between;
  position: fixed;
  top: 0;
  left: 0;
`;
const Logo = styled.figure`
  width: 180px;
  height: 33.5px;
  cursor: pointer;
`;
const Menu = styled.nav`
  display: grid;
  grid-template-columns: 72px 60px 48px;
  font-size: 13px;
  font-weight: bold;
  column-gap: 45px;
`;
function Header() {
  const location = useLocation();
  const [isScroll, setIsScroll] = useState(false);
  useEffect(() => {
    console.log(location.pathname);
    if (location.pathname === "/") {
      document.getElementById("root").addEventListener("scroll", (e) => {
        if (e.target.scrollTop !== 0) {
          setIsScroll(true);
        } else {
          setIsScroll(false);
        }
      });
    }
    return () => {
      if (location.pathname === "/") {
        document.getElementById("root").removeEventListener("scroll");
      }
    };
  }, []);
  return (
    <Wrapper>
      <Link to={"/"}>
        <Logo>
          <img
            src="/assets/header/logo.png"
            srcSet="/assets/header/logo@2x.png 2x , /assets/header/logo@3x.png 3x"
            alt="김과장컨설팅"
          />
        </Logo>
      </Link>
      <Menu>
        {menuarr.map(({ title, link }, idx) => {
          return (
            <Link
              key={idx}
              to={link}
              style={{
                color: link === location.pathname ? "#00be83" : "black",
              }}
            >
              {title}
            </Link>
          );
        })}
      </Menu>
    </Wrapper>
  );
}

export default Header;
