import React, { useRef } from "react";

function TeamInput({
  data: {
    number: { first, second },
    year,
    index,
  },
}) {
  const firstRef = useRef(null);
  const secondRef = useRef(null);
  const yearRef = useRef(null);
  return (
    <div className={`team-input ${index === 0 ? "zero" : ""}`}>
      <div className="id-wrapper">
        <input
          type="number"
          placeholder="2자리"
          className="first"
          ref={firstRef}
          onChange={(e) => {
            const val = e.target.value;
            if (val.length > 2) {
              firstRef.current.value = val.slice(0, 2);
            }
          }}
        />
        <div className="circle-wrapper">
          {[0, 0, 0, 0].map((item, idx) => {
            return <div key={idx} className="circle" />;
          })}
        </div>
        <div className="line" />
        <input
          type="number"
          className="second"
          ref={secondRef}
          onChange={(e) => {
            const val = e.target.value;
            if (val.length > 1) {
              secondRef.current.value = val.slice(0, 1);
            }
          }}
        />
        <div className="circle-wrapper">
          {[0, 0, 0, 0, 0].map((item, idx) => {
            return <div key={idx} className="circle" />;
          })}
        </div>
      </div>
      <div className="year-wrapper">
        <input type="text" placeholder="YYYY-MM" ref={yearRef} />
      </div>
    </div>
  );
}

export default TeamInput;
