import React, { useCallback, useReducer } from "react";
import Infoinput from "./components/Infoinput";
import SelectInput from "./components/SelectInput";
import Speech from "./components/Speech";
import TeamInput from "./components/TeamInput";
import "./css/index.css";
function reducer(state, action) {
  switch (action.type) {
    case "company":
      return { ...state, company: action.company };
    case "number":
      return { ...state, number: action.number };
    case "history":
      return { ...state, history: action.history };
    case "sector":
      return { ...state, sector: action.sector };
    case "region":
      return { ...state, region: action.region };
    case "team":
      return { ...state, team: action.payload };
    case "teamarr":
      return { ...state, teamarr: action.payload };
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}
function Request() {
  const [info, dispatch] = useReducer(reducer, {
    company: "",
    number: "",
    history: "",
    sector: "",
    region: "",
    team: 0,
    teamarr: [
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
    ],
    plan: 0,
    category: "",
    etc: {
      email: "",
      tel: "",
      policy: false,
    },
  });
  const __company = useCallback(
    (e) => {
      dispatch({
        type: "company",
        company: e,
      });
    },
    [dispatch]
  );
  const __number = useCallback(
    (e) => {
      dispatch({
        type: "number",
        number: e,
      });
    },
    [dispatch]
  );

  const __history = useCallback(
    (e) => {
      dispatch({
        type: "history",
        history: e,
      });
    },
    [dispatch]
  );
  const __sector = useCallback((e) => {
    dispatch({
      type: "sector",
      sector: e,
    });
  }, []);

  const __region = useCallback((e) => {
    dispatch({
      type: "region",
      region: e,
    });
  }, []);
  const __team = useCallback(
    (e) => {
      const arr = Array(parseInt(e)).fill();
      let arrobj = [];
      for (let i = 0; i < arr.length; i++) {
        arrobj.push(
          info.teamarr[i]
            ? info.teamarr[i]
            : {
                number: {
                  first: undefined,
                  second: undefined,
                },
                year: undefined,
              }
        );
      }
      dispatch({
        type: "team",
        payload: e,
      });
      dispatch({
        type: "teamarr",
        payload: arrobj,
      });
    },
    [dispatch, info]
  );
  return (
    <div className="request">
      <div className="top">
        <div className="wrapper">
          <div className="left">
            <div className="category">상담신청</div>
            <div className="title">
              정부지원금으로 스마트하게 <br /> 사업 운영하는 노하우!
            </div>
            <div className="sub">
              김과장컨설팅은 고객님의 사업장에 최적화된 <br /> 경영지원 서비스를
              제공합니다.
            </div>
          </div>
          <div className="right">
            <img src="/assets/request/document.svg" alt="" />
          </div>
        </div>
      </div>
      <div className="content">
        <div className="wrapper">
          <div className="notice">
            <div className="circle" />
            <div className="text">
              필수입력 항목입니다. 입력항목에 따라, 사업안내가 제한적일 수
              있습니다 .
            </div>
          </div>
          <div className="section1">
            <div className="content-head">
              <img src="/assets/request/company.svg" alt="" />
              <div className="text">기업정보</div>
            </div>
            <div className="company-wrapper">
              <div className="left">
                {section1Input.map(
                  ({ title, type, content, placeholder, arr }, idx) => {
                    return content !== "select" ? (
                      <Infoinput
                        key={idx}
                        title={title}
                        content={content}
                        placeholder={placeholder}
                        dispatch={type === "company" ? __company : __number}
                      />
                    ) : (
                      <SelectInput
                        key={idx}
                        title={title}
                        type={type}
                        content={content}
                        placeholder={placeholder}
                        arr={arr}
                        info={
                          type === "history"
                            ? info.history
                            : type === "sector"
                            ? info.sector
                            : info.region
                        }
                        dispatch={
                          type === "history"
                            ? __history
                            : type === "sector"
                            ? __sector
                            : __region
                        }
                      />
                    );
                  }
                )}
              </div>
              <div className="right">
                {section1Speech.map((item, idx) => {
                  return <Speech key={idx} content={item} />;
                })}
              </div>
            </div>
          </div>
          <div className="section2">
            <div className="content-head">
              <img src="/assets/request/team.svg" alt="" />
              <div className="text">팀원정보</div>
            </div>
            <div className="section-wrapper">
              <div className="left">
                <Infoinput
                  title={"4대보험 가입자 수"}
                  content={"number"}
                  sub={"(대표자제외)"}
                  type="team"
                  placeholder={"최대 20명"}
                  dispatch={__team}
                />
              </div>
              <div className="right">
                <Speech
                  content={
                    <div className="text">
                      4대보험 가입자 수에 따라 수혜가능한 지원사업이 달라집니다.{" "}
                      <br />
                      정확한 가입자 수를 입력해주세요.
                    </div>
                  }
                />
              </div>
            </div>
            <div className="team-wrapper">
              <div className="title">팀원 정보</div>
              <div className="team-box">
                <div className="tag-wrapper">
                  <div className="number">주민등록번호 (출생연도/성별)</div>
                  <div className="year">고용보험 등재 연월</div>
                </div>
                {info.teamarr.map((item, idx) => {
                  return <TeamInput key={idx} data={item} index={idx} />;
                })}
              </div>
            </div>
            <div className="plan-wrapper">
              <Infoinput
                title={"채용예정자"}
                content={"number"}
                type="plan"
                placeholder={"숫자만 입력"}
                dispatch={__team}
              />
            </div>
          </div>
          <div className="section3">
            <div className="content-head">
              <img src="/assets/request/kim.svg" alt="" />
              <div className="text">지원 희망 분야</div>
            </div>
            <div className="sector-wrapper">
              <div className="sub">
                지원 희망 분야를 선택해주세요 더욱 구체적인 상담을
                진행해드립니다
              </div>
              <div className="sector-select"></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Request;

const section1Input = [
  {
    title: "회사명",
    type: "company",
    content: "text",
    placeholder: "사업자등록증에 표기된 회사명  입력",
  },
  {
    title: "사업자등록번호",
    type: "number",
    content: "number",
    placeholder: "- 없이 숫자만 입력해주세요",
  },
  {
    title: "업력",
    type: "history",
    content: "select",
    placeholder: "업력 선택",
    arr: ["예비창업자", "1년 미만", "3년 미만", "7년 미만", "7년 이상"],
  },
  {
    title: "업종",
    type: "sector",
    content: "select",
    placeholder: "업종 선택",
    arr: [
      {
        title: "제조업",
        img: <img src="/assets/request/sector1.svg" alt="" />,
      },
