import React from "react";
import styled from "styled-components";
const arr = [
  {
    img: "assets/about2-1.svg",
    title: `온라인
상담 의뢰`,
  },
];
const Wrapper = styled.section`
  width: 100%;
  height: 1048px;
  & > div {
    width: 993px;
    height: 100%;
    margin: 0 auto;
    padding-top: 130.7px;
    box-sizing: border-box;
    & > .logo {
      background-image: url("/assets/greysteach.svg");
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
      width: 113px;
      height: 44.1px;
      font-size: 16px;
      font-weight: bold;
      color: #434343;
      display: flex;
      justify-content: center;
      align-items: center;
    }
    & > .title {
      margin-top: 20.5px;
      font-size: 40px;
      line-height: 1.38;
      color: #434343;
      font-family: "ygo";
    }
  }
`;
function Section3() {
  return (
    <Wrapper>
      <div>
        <div className="logo">진행 과정</div>
        <div className="title">
          김과장 컨설팅은 <br /> 이렇게 진행됩니다
        </div>
      </div>
    </Wrapper>
  );
}

export default Section3;
