                {section1Input.map(
                  ({ title, type, content, placeholder, arr }, idx) => {
                    return content !== "select" ? (
                      <Infoinput
                        key={idx}
                        title={title}
                        content={content}
                        placeholder={placeholder}
                        dispatch={type === "company" ? __company : __number}
                      />
                    ) : (
                      <SelectInput
                        key={idx}
                        title={title}
                        type={type}
                        content={content}
                        placeholder={placeholder}
                        arr={arr}
                        info={
                          type === "history"
                            ? info.history
