import * as actions from "../actions";

const initialState = {
  ask: {
    name: "",
    category: "",
    tel: "",
    email: "",
    number: 0,
  },
};

const database = (state = initialState, action) => {
  switch (action.type) {
    case "db/name":
      return {
        ...state,
        payload: action.payload,
      };
    case "db/category":
      return {
        ...state,
        payload: action.payload,
      };
    case "db/tel":
      return {
        ...state,
        payload: action.payload,
      };
    case "db/email":
      return {
        ...state,
        payload: action.payload,
      };
    case "db/number":
      return {
        ...state,
        payload: action.payload,
      };
    default:
      return state;
  }
};
export default database;
