import React from "react";
import Main from "./Main/Main";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "../css/core.css";
import About from "./About/About";
import Service from "./Service/Service";
import Support from "./Support/Support";
import styled from "styled-components";
function App() {
  return (
    <Test>
      <div className="wrapper">
        <div className="top">
          <img
            src="/assets/header/logo.png"
            srcSet="/assets/header/logo@2x.png 2x, /assets/header/logo@3x.png 3x"
            alt=""
          />
        </div>
        <div className="content">
          <div className="logo">
            <img
              src="/assets/test/test.png"
              srcSet="/assets/test/test@2x.png 2x, /assets/test/test@3x.png 3x"
              alt=""
            />
          </div>
          <div className="title">홈페이지 리뉴얼 중입니다</div>
          <div className="title green">블로그에서 무료상담 신청해보세요</div>
          <div className="content">
            보다 나은 서비스 제공을 위하여 리뉴얼 중입니다. <br /> 현재 김과장
            컨설팅 블로그에서 무료상담 서비스를 진행 중이니 <br /> 아래 링크를
            통해 무료상담을 신청해주시기바랍니다 :)
          </div>
          <button>
            <div>김과장 컨설팅 무료상담하기</div>
            <img src="/assets/test/white-arrow.svg" alt="" />
          </button>
        </div>
      </div>
    </Test>
    // <Router>
    //   <Routes>
    //     <Route path="/" exact element={<Main />} />
    //     <Route path="/about" exact element={<About />} />
    //     <Route path="/service" exact element={<Service />} />
    //     <Route path="/support" exact element={<Support />} />
    //   </Routes>
    // </Router>
  );
}

export default App;

const Test = styled.main`
  width: 100%;
  height: 100%;
  & > .wrapper {
    width: 1366px;
    margin: 0 auto;
    height: calc(100% - 64px);
    padding-left: 75px;
    & > .top {
      width: 180px;
      height: 64px;
      display: flex;
      align-items: center;
    }
    & > .logo {
      width: 152.4px;
      height: 166px;
      margin-bottom: 36px;
    }
    & > .content {
      width: 100%;
      height: 100%;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      & > .title {
        font-family: ygo;
        font-size: 40px;
      }
      & > .green {
        margin-top: 10px;
        color: #00be83;
      }
      & > .content {
        font-size: 17px;
        line-height: 1.59;
        text-align: center;
        margin: 30px 0 33px 0;
      }
      & > button {
        width: 305px;
        height: 54px;
        background-color: #00be83;
        border-radius: 57px;
        display: flex;
        align-items: center;
        box-sizing: border-box;
        justify-content: space-between;
        font-size: 18px;
        padding: 0 19px;
      }
    }
  }
`;
