import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import Footer from "../Footer/Footer";
import Header from "../Header/Header";
import Popup from "../Popup/Popup";
import Fold from "./components/Fold";
import "./css/index.css";
function Main() {
  const isPopup = useSelector((state) => state.config.isPopup);
  const [isScroll, setIsScroll] = useState(false);
  useEffect(() => {
    const ref = document.getElementById("root");
    function change(e) {
      if (e.target.scrollTop !== 0) {
        setIsScroll(true);
      } else {
        setIsScroll(false);
      }
    }
    ref.addEventListener("scroll", change);

    return () => {
      ref.removeEventListener("scroll", change);
    };
  }, []);
  return (
    <div style={{ width: "100%" }} className="mainsub">
      <header style={{ width: "100%" }}>
        <Header isScroll={isScroll} />
      </header>
      {/* <Side /> */}
      <div className="section">
        <div className="wrapper">
          <div className="title">
            복잡한 정부지원금 <br />
            <span>김과장과 함께 쉽게!</span>
          </div>
          <div className="box">
            <div className="text">
              <a href="/">내정부 지원금찾기</a>
            </div>
            <img src="/assets/sub/next.svg" alt="" />
          </div>
          <div className="back-img">
            <img
              src="/assets/sub/main.png"
              srcSet="/assets/sub/main@2x.png 2x, /assets/sub/main@3x.png 3x"
              alt=""
              className="pc"
            />
            <img
              src="/assets/sub/main-mb.png"
              srcSet="/assets/sub/main-mb@2x.png 2x, /assets/sub/main-mb@3x.png 3x"
              alt=""
              className="main-mb"
            />
          </div>
        </div>
      </div>
      <div className="section1">
        <div className="wrapper">
          <div className="title">내정부지원금을 찾아보세요!</div>
          <div className="box">
            {box.map((item, i) => {
              return (
                <div key={i} className={`boxlist ${item.type}`}>
                  <div className="title">{item.title}</div>
                  <div className="sub">{item.sub}</div>
                  <div className="img">{item.img}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <div className="section2">
        <div className="wrapper">
          <div className="wrapper2">
            <div className="left">
              <div className="title">
                믿고 받는 김과장컨설팅으로 <br />
                정부지원금 받으세요
              </div>
              <div className="title_tb">
                믿고 받는 <br />
                김과장컨설팅으로 <br />
                정부지원금 받으세요
              </div>
              <div className="box">
                <div className="text">
                  <a href="/">내정부 지원금찾기</a>
                </div>
                <img src="/assets/sub/next.svg" alt="" />
              </div>
            </div>
            <div className="right">
              <img
                src="/assets/sub/good.png"
                srcSet="/assets/sub/good@2x.png 2x, /assets/sub/good@3x.png 3x"
                alt=""
              ></img>
            </div>
          </div>
        </div>
      </div>
      <div className="section3">
        <div className="wrapper">
          <div className="title">
            김과장 컨설팅, <br /> 아직도 망설이시나요?
          </div>
          <div className="fold-wrapper">
            {foldarr.map(({ number, title, content }, idx) => {
              return (
                <Fold
                  key={idx}
                  content={content}
                  number={number}
                  title={title}
                />
              );
            })}
          </div>
        </div>
      </div>
      {isPopup ? <Popup /> : undefined}
    </div>
  );
}

export default Main;

