import React, { useCallback, useReducer } from "react";
import Infoinput from "./components/Infoinput";
import SectorCard from "./components/SectorCard";
import SelectInput from "./components/SelectInput";
import Speech from "./components/Speech";
import TeamInput from "./components/TeamInput";
import "./css/index.css";
function reducer(state, action) {
  switch (action.type) {
    case "company":
      return { ...state, company: action.company };
    case "number":
      return { ...state, number: action.number };
    case "history":
      return { ...state, history: action.history };
    case "sector":
      return { ...state, sector: action.sector };
    case "region":
      return { ...state, region: action.region };
    case "team":
      return { ...state, team: action.payload };
    case "plan":
      return { ...state, plan: action.payload };
    case "teamarr":
      return { ...state, teamarr: action.payload };
    case "category":
      return { ...state, category: action.payload };
    case "content":
      return { ...state, content: action.payload };
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}
function Request() {
  const [info, dispatch] = useReducer(reducer, {
    company: "",
    number: "",
    history: "",
    sector: "",
    region: "",
    team: 0,
    teamarr: [
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
      {
        number: {
          first: undefined,
          second: undefined,
        },
        year: undefined,
      },
    ],
    plan: 0,
    category: [],
    content: "",
    etc: {
      email: "",
      tel: "",
      policy: false,
    },
  });
  console.log(info);
  const __company = useCallback(
    (e) => {
      dispatch({
        type: "company",
        company: e,
      });
    },
    [dispatch]
  );
  const __number = useCallback(
    (e) => {
      dispatch({
        type: "number",
        number: e,
      });
    },
    [dispatch]
  );
  const __category = useCallback(
    (e) => {
      const arr = info.category.slice();
      const index = info.category.indexOf(e);
      if (index < 0) {
        arr.push(e);
      } else {
        arr.splice(index, 1);
      }
      dispatch({
        type: "category",
        payload: arr,
      });
    },
    [dispatch, info]
  );
  const __history = useCallback(
    (e) => {
      dispatch({
        type: "history",
        history: e,
      });
    },
    [dispatch]
  );
  const __sector = useCallback((e) => {
    dispatch({
      type: "sector",
      sector: e,
    });
  }, []);
  const __plan = useCallback(
    (e) => {
      dispatch({
        type: "plan",
        payload: parseFloat(e),
      });
    },
    [dispatch]
  );
  const __region = useCallback((e) => {
    dispatch({
      type: "region",
      region: e,
    });
  }, []);
  const __team = useCallback(
    (e) => {
      const arr = Array(parseInt(e)).fill();
      let arrobj = [];
      for (let i = 0; i < arr.length; i++) {
        arrobj.push(
          info.teamarr[i]
            ? info.teamarr[i]
            : {
                number: {
                  first: undefined,
                  second: undefined,
                },
                year: undefined,
              }
        );
      }
      dispatch({
        type: "team",
        payload: e,
      });
      dispatch({
        type: "teamarr",
        payload: arrobj,
      });
    },
    [dispatch, info]
  );
  return (
    <div className="request">
      <div className="top">
        <div className="wrapper">
          <div className="left">
            <div className="category">상담신청</div>
            <div className="title">
              정부지원금으로 스마트하게 <br /> 사업 운영하는 노하우!
            </div>
            <div className="sub">
              김과장컨설팅은 고객님의 사업장에 최적화된 <br /> 경영지원 서비스를
              제공합니다.
            </div>
          </div>
          <div className="right">
            <img src="/assets/request/document.svg" alt="" />
          </div>
        </div>
      </div>
      <div className="content">
        <div className="wrapper">
          <div className="notice">
            <div className="circle" />
