import React from "react";
import Header from "../Header/Header";
import styled from "styled-components";
import { formatDate } from "../lib/factory";

const dummy = [
  {
    title: "화성그랜드파크",
    sub: "",
    timestamp: Date.now(),
  },
  {
    title: "화성그랜드파크",
    sub: "",
    timestamp: Date.now(),
  },
  {
    title: "화성그랜드파크",
    sub: "",
    timestamp: Date.now(),
  },
  {
    title: "화성그랜드파크",
    sub: "",
    timestamp: Date.now(),
  },
];
const List = styled.div`
  width: 100%;
  height: 100%;
  margin-top: 33px;
`;
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  .title {
    font-size: 30px;
    font-weight: bold;
  }
  .right {
    transform: translateY(10px);
    display: flex;

    .input-wrapper {
      width: 215px;
      height: 44px;
      border: solid 1px #dbdbdb;
      border-radius: 5px;
      display: flex;
      overflow: hidden;
      padding-left: 13px;
      padding-right: 16px;
      box-sizing: border-box;
      img {
        width: 21px;
        cursor: pointer;
      }
      input {
        margin-right: 10px;
        width: 100%;
        height: 100%;
        font-size: 16px;
        font-weight: 500;
      }
      input::placeholder {
        font-size: 16px;
        font-weight: 500;
        color: #a8a8a8;
      }
    }
    .btn {
      width: 153px;
      height: 44px;
      background-color: #e0e0e0;
      border-radius: 6px;
      display: flex;
      justify-content: center;
      align-items: center;
      margin-left: 15px;
      font-size: 16px;
      font-weight: bold;
      color: #434343;
      cursor: pointer;
    }
  }
`;
const Body = styled.div`
  width: 992px;
  height: 100%;
  margin: 0 auto;
  padding-top: 53px;
  box-sizing: border-box;
`;
function Main() {
  return (
    <div>
      <Header />
      <Body>
        <Top>
          <div className="title">공사실적 관리</div>
          <div className="right">
            <div className="input-wrapper">
              <input type="text" placeholder="프로젝트명 검색" />
              <img src="/assets/grey-search.svg" alt="검색" />
            </div>
            <div className="btn">공사실적 추가</div>
          </div>
        </Top>
        <List>
          {dummy.map(({ title, timestamp }, idx) => {
            return (
              <div key={idx} className="card">
                <div className="left">
                  <div className="title">{title}</div>
                  <div className="time">{formatDate(timestamp, ".")}</div>
                </div>
                <div className="right">
                  <div className="fix">수정하기</div>
                  <div className="fix">수정하기</div>
                </div>
              </div>
            );
          })}
        </List>
      </Body>
    </div>
  );
}

export default Main;
