import React, { useEffect, useRef } from "react";
import firebaseApp from "../../../config/firebaseApp";
import TemplateEmty from "./TemplateEmty";

const Fstore = firebaseApp.firestore();
function TemplateImage({
  data: { resize, url, height },
  provided,
  setFocus,
  focusIdx,
  idx,
  template,
}) {
  const imgRef = useRef(null);
  useEffect(() => {
    if (!height && imgRef.current) {
      console.log(imgRef);
      console.log((1080 / 720) * 100);
    }

    return () => {};
  }, [height]);
  return (
    <>
      {idx === 0 ? <TemplateEmty idx={idx} /> : undefined}
      <div
        className={`template-img ${focusIdx === idx ? "active-blue" : ""}`}
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        onClick={() => {
          setFocus(idx);
        }}
      >
        <img src={url} alt="이미지" ref={imgRef} />
      </div>
      {!template[idx + 1] || template[idx + 1].type !== "TITLE" ? (
        <TemplateEmty idx={idx + 1} />
      ) : undefined}
    </>
  );
}

export default TemplateImage;
