import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 1024px;
  .top {
    display: flex;
    align-items: center;
    justify-content: space-between;
    .title {
      font-size: 30px;
      font-weight: bold;
    }
    .btn {
      width: 148px;
      height: 50px;
      cursor: pointer;
      background-color: #a50006;
      border-radius: 5px;
      color: white;
      font-size: 19px;
      font-weight: bold;
      display: flex;
      justify-content: center;
      align-items: center;
    }
  }
  .ti {
    font-size: 19px;
    font-weight: bold;
    margin-top: 30px;
  }
  input {
    width: 100%;
    height: 50px;
    border: solid 1px #dbdbdb;
    border-radius: 5px;
    font-size: 17px;
  }
`;

function TitleSection({ category }) {
  return (
    <Wrapper>
      <div className="top">
        <div className="title">
          {category === "portfolio" ? "공사실적" : "공지사항"}추가
        </div>
        <div className="btn">등록</div>
      </div>

      <div className="ti">게시글 제목</div>
      <input
        type="text"
        maxLength={45}
        placeholder="게시글 제목을 입력해주세요"
      />

      <div className="ti">개요</div>
      <input type="text" maxLength={45} />
    </Wrapper>
  );
}

export default TitleSection;
