import React, { useState, useEffect, useCallback } from "react";
import "./css/index.css";
import EdiHeader from "./components/EdiHeader";
import Screen from "./components/Screen";
import Popup from "./components/Popup";
import Header from "../Header/Header";
import { useDispatch, useSelector } from "react-redux";
import { Beforeunload, useBeforeunload } from "react-beforeunload";
import { Prompt } from "react-router-dom";
import firebaseApp from "../config/firebaseApp";
const Fstore = firebaseApp.firestore();
function Editor({ location }) {
  const dispatch = useDispatch();
  const temKey = useSelector((state) => state.database.key);
  const template = useSelector((state) => state.database.editor);

  const [isUp, setIsUp] = useState({
    status: false,
    type: "",
  });
  const __updateData = useCallback(() => {
    Fstore.collection("editor").doc(temKey).update({ template: template });
    console.log("update");
    console.log(template);
  }, [temKey, template]);
  useBeforeunload((e) => {
    __updateData();
    console.log("무슨일이고?");
    e.preventDefault();
  });

  useEffect(() => {
    const { type, timestamp, category, id } = location.state;
    if (type === "new") {
      Fstore.collection("editor")
        .add({
          timestamp: timestamp,
          state: category,
        })
        .then((res) => {
          dispatch({
            type: "@layouts/INIT_KEY",
            payload: res.id,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      Fstore.collection("editor")
        .doc(id)
        .get()
        .then((result) => {
          console.log(result.data());
          dispatch({
            type: "@layouts/CHANGE_EDITOR",
            payload: result.data().template,
          });
        });
      dispatch({
        type: "@layouts/INIT_KEY",
        payload: id,
      });
    }
    return () => {};
  }, [dispatch, location]);
  return (
    <Beforeunload
      onBeforeunload={(e) => {
        __updateData();
        e.preventDefault();
      }}
    >
      <Prompt
        message={() => {
          __updateData();
          return "템플릿을 저장하고 나가시겠습니까?";
        }}
      />
      <div className="editor">
        <Header />
        <div className="editor-wrapper">
          <EdiHeader setIsUp={setIsUp} temKey={temKey} />
          <Screen temKey={temKey} />
        </div>
        <Popup isUp={isUp} setIsUp={setIsUp} temKey={temKey} />
      </div>
    </Beforeunload>
  );
}

export default Editor;
