import React, { useRef, useCallback } from "react";
import TemplateEmty from "./TemplateEmty";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import firebaseApp from "../../../config/firebaseApp";
import Resizer from "react-image-file-resizer";
const Fstorage = firebaseApp.storage();
const Label = styled.div`
  width: 143px;
  height: 139px;
  background-color: #f7f7f7;
  border: solid 1px #dbdbdb;
  .list-img {
    width: 58px;
    height: 59px;
  }
  label {
    position: relative;
    display: flex;
    width: 100%;
    height: 100%;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    input {
      position: absolute;
      display: none;
    }
  }
`;
const Wrapper = styled.div`
  width: 100%;
  height: 550px;
  display: flex;
  border-bottom: solid 1px #dbdbdb;
  .main {
    border: solid 1px #dbdbdb;
    margin-right: 35px;
    width: 503px;
    height: 490px;
    background-color: #f7f7f7;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    label {
      position: relative;
      display: flex;
      width: 100%;
      height: 100%;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      cursor: pointer;
      input {
        position: absolute;
        display: none;
      }
      .true-image {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
      }
    }
    .sub {
      margin-top: 11px;
      font-size: 14px;
      font-weight: normal;
      color: #bfbfbf;
      text-align: center;
      line-height: 1.57;
    }
    img {
      width: 73px;
    }
  }
  .content {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding-bottom: 60px;

    box-sizing: border-box;
    p {
      overflow: hidden;
      text-overflow: ellipsis;
      cursor: text;
      width: 454px;
      height: auto;
      white-space: pre-wrap;
      word-wrap: break-word;
      font-size: 17px;
    }
    textarea::placeholder {
      color: #bfbfbf;
      font-size: 17px;
    }
    .list {
      display: grid;
      grid-template-columns: repeat(3, 143px);
      column-gap: 14px;
      margin-top: 25px;
    }
  }
`;

function Summary({
  data: { text, images },
  provided,
  template,
  idx,
  setFocus,
  id,
}) {
  const status = useSelector((state) => state.test.editor);
  const dispatch = useDispatch();
  const contentRef = useRef(null);

  const __imageUpload = useCallback(
    (data64, name) => {
      return new Promise((resolve, reject) => {
        const data = data64.split(",")[1];
        Fstorage.ref(`editor/${id}/${name}`)
          .putString(data, "base64")
          .then((result) => {
            result.ref.getDownloadURL().then((downloadUrl) => {
              resolve(downloadUrl);
            });
          });
      });
    },
    [id]
  );
  const __fileReader = useCallback((file) => {
    return new Promise((resolve, reject) => {
      var reader = new FileReader();
      reader.onload = function (e) {
        const imageUrl = e.target.result;
        Resizer.imageFileResizer(
          file,
          100,
          100,
          "PNG",
          100,
          0,
          (uri) => {
            resolve({ url: imageUrl, resize: uri });
          },
          "base64"
        );
      };
      reader.readAsDataURL(file);
    });
  }, []);
  const __updateImage = useCallback(
    (type, file) => {
      __fileReader(file).then((res) => {
        Promise.all([
          __imageUpload(res.resize, `${type}-resize`).then((res) => {
            return res;
          }),
          __imageUpload(res.url, type).then((res) => {
            return res;
          }),
        ]).then((result) => {
          console.log(result);
          dispatch({
            type: "@layouts/CHANGE_SUMMARY_IMAGE",
            idx,
            payload: {
              img: result[1],
              resize: result[0],
            },
            index: 0,
          });
        });
      });
    },
    [__fileReader, __imageUpload, dispatch, idx]
  );
  return (
    <>
      {idx === 0 ? <TemplateEmty idx={idx} /> : undefined}
      <div
        className="template-video"
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        onClick={() => {
          setFocus(idx);
        }}
      >
        <div className="dnd-icon" style={{ right: "-60px" }}>
          <img
            src="/assets/projects/dnd-icon.svg"
            alt="원하는 위치로 이동해보세요!"
          />
        </div>
        <Wrapper>
          <div className="main">
            <label>
              {images[0] ? (
                <img className="true-image" src={images[0].img} alt="" />
              ) : undefined}
              <img
                className="main-img"
                src="/assets/editor/grey-plus.png"
                alt="추가"
              />
              <input
                type="file"
                accept="image/x-png,image/gif,image/jpeg"
                onChange={(e) => {
                  __updateImage("main", e.target.files[0]);
                }}
              />
              <div className="sub">
                사진을 업로드해주세요 <br /> jpeg, png, gif
              </div>
            </label>
          </div>
          <div className="content">
            <p
              contentEditable={true}
              ref={contentRef}
              suppressContentEditableWarning={true}
              onKeyDown={(e) => {
                if (contentRef.current.offsetHeight > 300 && e.keyCode !== 8) {
                  const test = contentRef.current.childNodes;
                  test[test.length - 1].remove();
                }
              }}
              onInput={(e) => {
                dispatch({
                  type: "@layouts/CHANGE_SUMMARY",
                  payload: contentRef.current.innerHTML,
                  idx,
                });
              }}
              onSelect={(e) => {
                const select = window.getSelection();
                const style = window.getComputedStyle(
                  select.focusNode.parentNode
                );
                const fontSize = parseInt(style.fontSize);
                const align = style.textAlign;
                const bold = document.queryCommandState("bold");
                const underline = document.queryCommandState("underline");
                const color = document.queryCommandValue("ForeColor");
                if (
                  select.focusNode.localName === undefined &&
                  fontSize !== status.size
                ) {
                  dispatch({
                    type: "@test/CHANGE_SIZE",
                    payload: fontSize,
                  });
                }
                if (underline !== status.underline) {
                  dispatch({
                    type: "@test/CHANGE_UNDER",
                    payload: underline,
                  });
                }
                if (bold !== status.bold) {
                  dispatch({
                    type: "@test/CHANGE_BOLD",
                    payload: bold,
                  });
                }
                if (align !== status.align && align !== "start") {
                  dispatch({
                    type: "@test/CHANGE_ALIGN",
                    payload: align,
                  });
                }
                if (color !== status.color) {
                  dispatch({
                    type: "@test/CHANGE_COLOR",
                    payload: color,
                  });
                }
              }}
            ></p>
            <div className="list">
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
            </div>
          </div>
        </Wrapper>
      </div>
      {!template[idx + 1] || template[idx + 1].type !== "TITLE" ? (
        <TemplateEmty idx={idx + 1} />
      ) : undefined}
    </>
  );
}

export default Summary;
