import React, { useRef, useState } from "react";
import TemplateEmty from "./TemplateEmty";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";

const Label = styled.div`
  width: 143px;
  height: 139px;
  background-color: #f7f7f7;
  border: solid 1px #dbdbdb;
  .list-img {
    width: 58px;
    height: 59px;
  }
  label {
    position: relative;
    display: flex;
    width: 100%;
    height: 100%;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    input {
      position: absolute;
      display: none;
    }
  }
`;
const Wrapper = styled.div`
  width: 100%;
  height: 550px;
  display: flex;
  border-bottom: solid 1px #dbdbdb;
  .main {
    border: solid 1px #dbdbdb;
    margin-right: 35px;
    width: 503px;
    height: 490px;
    background-color: #f7f7f7;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    label {
      position: relative;
      display: flex;
      width: 100%;
      height: 100%;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      cursor: pointer;
      input {
        position: absolute;
        display: none;
      }
    }
    .sub {
      margin-top: 11px;
      font-size: 14px;
      font-weight: normal;
      color: #bfbfbf;
      text-align: center;
      line-height: 1.57;
    }
    img {
      width: 73px;
    }
  }
  .content {
    position: relative;
    padding-bottom: 60px;
    box-sizing: border-box;
    p {
      width: 454px;
      height: auto;
      white-space: pre-wrap;
      word-wrap: break-word;
      font-size: 17px;
    }
    textarea::placeholder {
      color: #bfbfbf;
      font-size: 17px;
    }
    .list {
      display: grid;
      grid-template-columns: repeat(3, 143px);
      column-gap: 14px;
      margin-top: 25px;
    }
  }
`;
function Summary({ data, provided, template, idx, setFocus }) {
  const status = useSelector((state) => state.test.editor);
  const dispatch = useDispatch();
  const contentRef = useRef(null);
  const [Tval, setTval] = useState("");
  return (
    <>
      {idx === 0 ? <TemplateEmty idx={idx} /> : undefined}
      <div
        className="template-video"
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        onClick={() => {
          setFocus(idx);
        }}
      >
        <div className="dnd-icon" style={{ right: "-60px" }}>
          <img
            src="/assets/projects/dnd-icon.svg"
            alt="원하는 위치로 이동해보세요!"
          />
        </div>
        <Wrapper>
          <div className="main">
            <label>
              <img
                className="main-img"
                src="/assets/editor/grey-plus.png"
                alt="추가"
              />
              <input type="file" accept="image/x-png,image/gif,image/jpeg" />
              <div className="sub">
                사진을 업로드해주세요 <br /> jpeg, png, gif
              </div>
            </label>
          </div>
          <div className="content">
            <p
              contentEditable={true}
              ref={contentRef}
              suppressContentEditableWarning={true}
              onFocus={() => {
                setFocus(idx);
              }}
              onInput={(e) => {
                console.log(contentRef.current.offsetHeight);
                dispatch({
                  type: "@layouts/CHANGE_TITLE",
                  payload: contentRef.current.innerHTML,
                  idx,
                });
              }}
              onSelect={(e) => {
                const select = window.getSelection();
                const style = window.getComputedStyle(
                  select.focusNode.parentNode
                );
                const fontSize = parseInt(style.fontSize);
                const align = style.textAlign;
                const bold = document.queryCommandState("bold");
                const underline = document.queryCommandState("underline");
                const color = document.queryCommandValue("ForeColor");
                if (
                  select.focusNode.localName === undefined &&
                  fontSize !== status.size
                ) {
                  dispatch({
                    type: "@test/CHANGE_SIZE",
                    payload: fontSize,
                  });
                }
                if (underline !== status.underline) {
                  dispatch({
                    type: "@test/CHANGE_UNDER",
                    payload: underline,
                  });
                }
                if (bold !== status.bold) {
                  dispatch({
                    type: "@test/CHANGE_BOLD",
                    payload: bold,
                  });
                }
                if (align !== status.align && align !== "start") {
                  dispatch({
                    type: "@test/CHANGE_ALIGN",
                    payload: align,
                  });
                }
                if (color !== status.color) {
                  dispatch({
                    type: "@test/CHANGE_COLOR",
                    payload: color,
                  });
                }
              }}
            ></p>
            {/* <div
              ref={SpanRef}
              style={{
                width: "454px",
                height: "auto",
                opacity: 0,
                whiteSpace: "pre-wrap",
                wordWrap: "break-word",
                position: "absolute",
                fontSize: "17px",
              }}
            >
              {Tval}
            </div> */}
            <div className="list">
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
              <Label>
                <label>
                  <img
                    className="list-img"
                    src="/assets/editor/grey-plus.png"
                    alt="추가"
                  />
                  <input
                    type="file"
                    accept="image/x-png,image/gif,image/jpeg"
                  />
                </label>
              </Label>
            </div>
          </div>
        </Wrapper>
      </div>
      {!template[idx + 1] || template[idx + 1].type !== "TITLE" ? (
        <TemplateEmty idx={idx + 1} />
      ) : undefined}
    </>
  );
}

export default Summary;
