import styled from "styled-components";

export const Animation = styled.div`
  @keyframes arrow-moving {
    from {
      opacity: 0.1;
      transform: translateY(30px);
    }

    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
  animation: arrow-moving 0.8s;
`;
